const inp_file=document.querySelector(".input-file-chat"),name_file=document.querySelector(".your-file-name h2"),size_file=document.querySelector(".your-file-name p"),file_box=document.querySelector(".your-file");inp_file.addEventListener("input",()=>{inp_file.files[0].size<=52428800?(file_box.classList.add("active"),name_file.append(inp_file.files[0].name),size_file.append(conv_size(inp_file.files[0].size))):(file_box.classList.add("active"),file_box.classList.add("error"))}),console.log(inp_file,name_file,size_file);const message_s=document.querySelectorAll(".users-name-message");function resizeP(){message_s.forEach(e=>{e.style.maxWidth=e.parentElement.parentElement.offsetWidth-e.parentElement.previousElementSibling.offsetWidth-e.parentElement.nextElementSibling.offsetWidth-28+"px"})}function conv_size(e){let i=e/1024,n=i/1024;return i<=1024?i.toFixed(3)+" кб":i>=1024&&n<=1024?n.toFixed(3)+" мб":void 0}resizeP(),window.addEventListener("resize",resizeP);